//Breakpoints
@media (min-width: 64em) {
    //styles go here
}

//690px, 1024px, 1400px
$breakpoints-up: (
    "medium": "43.125em",
    "large": "64em",
    "xlarge": "87.5em",
);

//689px, 1023px, 1399px
$breakpoints-down: (
    "small": "43.0625em",
    "medium": "63.9375em",
    "large": "87.4375em",
);

@mixin breakpoint-up($size) {
    @media (min-width: map-get($breakpoints-up, $size)) {
        @content;
    }
}
@mixin breakpoint-down($size) {
    @media (max-width: map-get($breakpoints-down, $size)) {
        @content;
    }
}

